    function onDragEnd(result) {
        if (!result.destination) {
            return;
        }
        const newBlocksList = reorder(blockList, result.source.index, result.destination.index);
        setBlockList(newBlocksList);
    }

    useEffect(() => {
        async function getBlocks() {
            try {
                let response = await fetch(`http://137.112.104.54:22051/documentblocks?uid=${userID}&docid=${docID}`);
