import { useEffect, useState, useRef } from 'react';
import './App.css';
import { NavLink, Routes, Route, useNavigate } from 'react-router-dom';
import { click } from '@testing-library/user-event/dist/click';

function App() {
    const [loggedIn, setLoggedIn] = useState(false);
    return (
        <div className="App">
            <Navigation />
            <Routes>
                <Route exact path="/" Component={Home} />
                <Route exact path="/about" Component={About} />
                <Route exact path="/howitworks" Component={HowItWorks} />
                <Route exact path="/faqs" Component={FAQs} />
                <Route exact path="/login" Component={() => Login(setLoggedIn)} />
                <Route exact path="/signup" Component={() => Signup(setLoggedIn)} />
                <Route exact path="/documents" Component={Documents} />
            </Routes>
        </div>
    );
}

const Navigation = () => (
    <nav>

        <ul className="navBar">
            {/* Could not get styling to be reset for the NavLinks withought using inline styling */}
            <li><NavLink to="/"><img src="TextBlock_LogoDark.png" alt="Logo" /></NavLink></li>
            <li className="textB"><NavLink to="/about" style={{ textDecoration: "none" }}>About Us</NavLink></li>
            <li class="textB"><NavLink to="/howitworks" style={{ textDecoration: "none" }}>How It Works</NavLink></li>
            <li class="textB"><NavLink to="/faqs" style={{ textDecoration: "none" }}>FAQs</NavLink></li>

        </ul>

        <ul className="in">
            <li class="boxB"><NavLink to="/login" style={{ textDecoration: "none" }}>Login</NavLink></li>
            <li class="boxB"><NavLink to="/signup" style={{ textDecoration: "none" }}>Sign Up</NavLink></li>
        </ul>

    </nav>
);

const Home = () => (
    <main>
        <h1>Quickly Create & Organize Documents With Module Text Blocks.</h1>
        <h2>A smarter way to build, organize, and refine your documents - flexible, efficient, and intuitive.</h2>
        <NavLink to="/signup">Get Started</NavLink>
    </main>
);

const About = () => (
    <main>
        <h1>About Us</h1>
    </main>
);

const HowItWorks = () => (
    <main>
        <h1>How It Works</h1>
    </main>
);

const FAQs = () => (
    <main>
        <h1>Frequently Asked Questions</h1>
    </main>
);

const FormItem = ({ ...props }) => (
    <>
        <p><label htmlFor={props.name}>{props.label}</label></p>
        <input
            type={props.type}
            name={props.name}
            id={props.id}
            maxLength={props.maxLength}
            minLength={props.minLength}
            value={props.value}
            required={props.required}
            onChange={props.onChange}>
        </input>
    </>
);

const Login = (setLoggedIn) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("")
    const [loginSuccess, setLoginSuccess] = useState(null);
    const navigate = useNavigate();
    async function handleLogin(event) {
        event.preventDefault();
        let response = await fetch("http://137.112.104.54:22051/users/login", {
            method: "POST",
            body: JSON.stringify({ username: username, password: password })
        });
        let responseJson = await response.json();
        setLoginSuccess(responseJson.success);
        console.log(responseJson.success);

        if (responseJson.success) {
            setLoggedIn(true);
            navigate("/documents");
        }
    }
    return (
        <main>
            <h1>Welcome Back</h1>
            {loginSuccess === false && <p style={{ color: 'red' }}>Username or Password is incorrect. Please try again.</p>}
            <form id='login'>
                <FormItem type='text' name='username' id='username' label='Username' required
                    onChange={(e) => setUsername(e.target.value)} />
                <FormItem type='password' name='password' id='password' label='Password' required
                    onChange={(e) => setPassword(e.target.value)} />
                <br />
                <button type='submit' onClick={handleLogin}>Log In</button>
            </form>
            <p>Don't already have an account? <NavLink to="/signup">Sign up</NavLink></p>
        </main>
    );
};

const Signup = (setLoggedIn) => {
    const [username, setUsername] = useState("");
    const passwordRef = useRef("");
    const [passwordValid, setPasswordValid] = useState(null);
    const [isUnique, setIsUnique] = useState(null);
    const pwRe = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#\$%\^&\*\(\)\-\+=\{\}\[\]:;"'<,>\.\?\/])[A-Za-z\d!@#\$%\^&\*\(\)\-\+=\{\}\[\]:;"'<,>\.\?\/~]{8,}$/; // oh god

    function checkPassword() {
        let valid = pwRe.test(passwordRef.current);
        console.log(passwordRef.current + " is " + (valid ? "valid" : "invalid") + ".");
        setPasswordValid(valid);
    }

    async function clickHandler(event) {
        event.preventDefault();
        let response = await fetch("")
        setLoggedIn(true);
    }

    useEffect(() => {
        if (username) {
            var checkUsername = async () => {
                try {
                    let response = await fetch(`http://137.112.104.54:22051/users/check?username=${username}`);
                    let data = await response.json();
                    setIsUnique(data.available);
                } catch {
                    console.log("Error checking username");
                }
            }
        }
        let timerId = setTimeout(checkUsername, 200);
        return () => clearTimeout(timerId);
    }, [username]);

    return (
        <main>
            <h1>Welcome to the Text Block Community</h1>
            <form method='POST' action='http://137.112.104.54:22051/users/new'>
                <FormItem type='email' name='email' id='email' label='Email' required />
                <FormItem type='text' name='username' id='username' label='Username' minLength='3' maxLength='20' required
                    onChange={(e) => {
                        setUsername(e.target.value);
                        setIsUnique(null);
                    }} />
                {isUnique === false && <p style={{ color: 'red' }}>Username is already taken.</p>}
                {isUnique && <p style={{ color: 'green' }}>Username is available!</p>}
                <FormItem type='password' name='password' id='password' label='Password' minLength='8' required
                    onChange={(e) => {
                        passwordRef.current = e.target.value;
                        checkPassword();
                    }} />
                {passwordValid === false && <p style={{ color: 'red' }}>Invalid password.</p>}
                <br />
                <button
                    type='submit'
                    disabled={!isUnique || !passwordValid}
                    onClick={clickHandler}>Create Account</button>
            </form>
            <p>Already have an account? <NavLink to="/login">Log in</NavLink></p>
        </main>
    );
}

